function radioButtonGroup(t){t.addEventListener("click",function(e){matchesSelector(e.target,"button")&&(t.querySelector(".is-checked").classList.remove("is-checked"),e.target.classList.add("is-checked"))})}var gallery=document.querySelector(".gallery"),iso=new Isotope(gallery,{itemSelector:".gallery-item",percentPosition:!0,masonry:{gutter:12}});imagesLoaded(gallery).on("progress",function(){iso.layout()});var filtersElem=document.querySelector(".filter-button-group");filtersElem.addEventListener("click",function(t){if(matchesSelector(t.target,"button")){var e=t.target.getAttribute("data-filter");iso.arrange({filter:e})}});for(var buttonGroups=document.querySelectorAll(".filter-button-group"),i=0,len=buttonGroups.length;i<len;i++){var buttonGroup=buttonGroups[i];radioButtonGroup(buttonGroup)}